mapper.select:
select ba.id, ba.publisher, ba.createTime, ba.articleTitle, ba.articleContent, ba.articlePicture, (select count(*) from blog_article_like_list as ball where ball.articleId = ba.id) as articleLikes, ba.articleViews, bum.avatar, bum.nickName
, (select count(*) from blog_article_like_list as ball where ball.articleId = #{articleId} and ball.uid = #{uid}) as hasLike
from blog_article as ba left join blog_user_means as bum on ba.publisher = bum.uid where ba.id = #{articleId}
